/*
 * 参数合并
 * 
 * <参数(obj),默认参数(obj),是否覆盖[true](boole)>
 */
module.exports = function (op, def, cover) {
    cover = (typeof (cover) != 'null' && typeof (cover) != 'undefined')?cover:true;
    
    if (!op || op == null) {
        return def;
    }
    if (!def || def == null) {
        return op;
    }
    //循环获取变量
    for (var i in op) {
        var tmp = op[i];
        if (typeof (def[i]) != 'undefined' && cover) {
            def[i] = tmp;
        }
    }
    
    return def;
} 